import 'package:flutter/material.dart';import '../model/cars_collection.dart';var textStyle = const TextStyle(fontFamily: 'Poppins');class DetailScreen extends StatelessWidget {  final CarsCollections carsCollections;  const DetailScreen({Key? key, required this.carsCollections})      : super(key: key);  @override  Widget build(BuildContext context) {    return LayoutBuilder(      builder: (BuildContext context, BoxConstraints constraints) {        if (constraints.maxWidth >= 800) {          return DetailWebPage(carsCollections: carsCollections);        } else {          return DetailMobilePage(carsCollections: carsCollections);        }      },    );  }}class DetailWebPage extends StatefulWidget {  final CarsCollections carsCollections;  const DetailWebPage({Key? key, required this.carsCollections})      : super(key: key);  @override  State<DetailWebPage> createState() => _DetailWebPageState();}class _DetailWebPageState extends State<DetailWebPage> {  final _scrollController = ScrollController();  @override  Widget build(BuildContext context) {    return Scaffold(      body: Padding(        padding: const EdgeInsets.symmetric(          vertical: 32,          horizontal: 64,        ),        child: Center(          child: SizedBox(            width: 1200,            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                const Text(                  'Cars Collection',                  style: TextStyle(                    fontFamily: 'Poppins',                    fontSize: 32,                  ),                ),                const SizedBox(height: 32),                Row(                  crossAxisAlignment: CrossAxisAlignment.start,                  children: [                    Expanded(                      child: Column(                        children: [                          ClipRRect(                            borderRadius: BorderRadius.circular(16),                            child:                                Image.asset(widget.carsCollections.imageAssets),                          ),                          const SizedBox(height: 16),                          Scrollbar(                            controller: _scrollController,                            child: Container(                              height: 150,                              padding: const EdgeInsets.only(bottom: 16),                              child: ListView(                                scrollDirection: Axis.horizontal,                                children:                                    widget.carsCollections.imageUrls.map((url) {                                  return Padding(                                    padding: const EdgeInsets.all(4.0),                                    child: ClipRRect(                                      borderRadius: BorderRadius.circular(10),                                      child: Image.network(url),                                    ),                                  );                                }).toList(),                              ),                            ),                          )                        ],                      ),                    ),                    const SizedBox(width: 32),                    Expanded(                      child: Flexible(                        child: Card(                          child: Container(                            padding: const EdgeInsets.all(16),                            child: Column(                              mainAxisSize: MainAxisSize.max,                              children: <Widget>[                                Text(                                  widget.carsCollections.name,                                  textAlign: TextAlign.center,                                  style: const TextStyle(                                    fontSize: 30.0,                                    fontFamily: 'Poppins',                                  ),                                ),                                const SizedBox(height: 32),                                Row(                                  mainAxisAlignment:                                      MainAxisAlignment.spaceBetween,                                  children: [                                    Row(                                      children: <Widget>[                                        const Icon(Icons.speed_rounded),                                        const SizedBox(width: 8.0),                                        Text(                                          widget.carsCollections.speed,                                          style: textStyle,                                        ),                                      ],                                    ),                                    const FavoriteButton(),                                  ],                                ),                                Row(                                  children: <Widget>[                                    const Icon(Icons.star_border_rounded),                                    const SizedBox(width: 8.0),                                    Text(                                      widget.carsCollections.rating,                                      style: textStyle,                                    ),                                  ],                                ),                                const SizedBox(height: 8.0),                                Row(                                  children: <Widget>[                                    const Icon(Icons.monetization_on_outlined),                                    const SizedBox(width: 8.0),                                    Text(                                      widget.carsCollections.price,                                      style: textStyle,                                    ),                                  ],                                ),                                Container(                                  padding: const EdgeInsets.symmetric(                                      vertical: 16.0),                                  child: Text(                                    widget.carsCollections.description,                                    textAlign: TextAlign.justify,                                    style: const TextStyle(                                      fontSize: 16.0,                                      fontFamily: 'Poppins',                                    ),                                  ),                                ),                              ],                            ),                          ),                        ),                      ),                    ),                  ],                ),              ],            ),          ),        ),      ),    );  }  @override  void dispose() {    _scrollController.dispose();    super.dispose();  }}class DetailMobilePage extends StatelessWidget {  final CarsCollections carsCollections;  const DetailMobilePage({Key? key, required this.carsCollections})      : super(key: key);  @override  Widget build(BuildContext context) {    return Scaffold(      body: SafeArea(        child: SingleChildScrollView(          child: Column(            crossAxisAlignment: CrossAxisAlignment.stretch,            children: <Widget>[              Stack(                children: [                  Image.asset(carsCollections.imageAssets),                  SafeArea(                    child: Padding(                      padding: const EdgeInsets.only(                          left: 16.0, top: 8.0, right: 16.0),                      child: Row(                        mainAxisAlignment: MainAxisAlignment.spaceBetween,                        children: [                          CircleAvatar(                            backgroundColor:                                const Color.fromRGBO(0, 0, 0, 99.0),                            child: IconButton(                              icon: const Icon(Icons.arrow_back_rounded),                              color: Colors.white,                              onPressed: () {                                Navigator.pop(context);                              },                            ),                          ),                          const CircleAvatar(                            backgroundColor: Color.fromRGBO(0, 0, 0, 99.0),                            child: FavoriteButton(),                          ),                        ],                      ),                    ),                  ),                ],              ),              Container(                margin: const EdgeInsets.only(top: 16.0, left: 16.0),                child: Text(                  carsCollections.name,                  textAlign: TextAlign.left,                  style: const TextStyle(                      fontSize: 30.0,                      fontWeight: FontWeight.bold,                      fontFamily: 'Poppins'),                ),              ),              Container(                margin: const EdgeInsets.symmetric(vertical: 16.0),                child: Row(                  mainAxisAlignment: MainAxisAlignment.spaceEvenly,                  children: <Widget>[                    Column(                      children: <Widget>[                        const Icon(Icons.speed_rounded),                        const SizedBox(height: 8.0),                        Text(                          carsCollections.speed,                          style: textStyle,                        ),                      ],                    ),                    Column(                      children: <Widget>[                        const Icon(Icons.star_border_outlined),                        const SizedBox(height: 8.0),                        Text(carsCollections.rating, style: textStyle),                      ],                    ),                    Column(                      children: <Widget>[                        const Icon(Icons.monetization_on),                        const SizedBox(height: 8.0),                        Text(                          carsCollections.price,                          style: textStyle,                        ),                      ],                    ),                  ],                ),              ),              Container(                padding: const EdgeInsets.all(16.0),                child: Text(                  carsCollections.description,                  textAlign: TextAlign.left,                  style: const TextStyle(fontSize: 14.0, fontFamily: 'Poppins'),                ),              ),              Container(                padding: const EdgeInsets.all(16.0),                child: const Text(                  'Foto Lainnya',                  textAlign: TextAlign.left,                  style: TextStyle(                      fontSize: 24.0,                      fontWeight: FontWeight.bold,                      fontFamily: 'Poppins'),                ),              ),              Container(                margin: const EdgeInsets.only(bottom: 32.0, left: 10.0, right: 10.0),                child: SizedBox(                  height: 150,                  child: ListView(                    scrollDirection: Axis.horizontal,                    children: carsCollections.imageUrls.map((url) {                      return Padding(                        padding: const EdgeInsets.all(4.0),                        child: ClipRRect(                          borderRadius: BorderRadius.circular(16),                          child: Image.network(url),                        ),                      );                    }).toList(),                  ),                ),              )            ],          ),        ),      ),    );  }}class FavoriteButton extends StatefulWidget {  const FavoriteButton({Key? key}) : super(key: key);  @override  _FavoriteButtonState createState() => _FavoriteButtonState();}class _FavoriteButtonState extends State<FavoriteButton> {  bool isFavorite = false;  @override  Widget build(BuildContext context) {    return IconButton(      onPressed: () {        setState(() {          isFavorite = !isFavorite;        });      },      icon: Icon(          isFavorite ? Icons.favorite_rounded : Icons.favorite_border_rounded),      color: Colors.red,    );  }}