import 'dart:async';import 'package:flutter/material.dart';import 'package:flutter_pemula/ui/home_screen.dart';class SplashScreen extends StatefulWidget {  const SplashScreen({super.key});  @override  _MySplashScreenState createState() => _MySplashScreenState();}class _MySplashScreenState extends State<SplashScreen> {  @override  void initState() {    super.initState();    Timer(        const Duration(seconds: 2),        () => Navigator.pushReplacement(context,            MaterialPageRoute(builder: (context) => const HomeScreen())));  }  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.black87,      body: SafeArea(        child: Column(          crossAxisAlignment: CrossAxisAlignment.stretch,          children: <Widget>[            Padding(                padding: const EdgeInsets.fromLTRB(0.0, 160.0, 0.0, 0.0),                child: Center(                    child: Image.asset('images/car_icon.png',                        width: 250.0, height: 100.0))),            const Text(              'Car Collection',              textAlign: TextAlign.center,              style: TextStyle(                  color: Colors.white,                  fontWeight: FontWeight.bold,                  fontSize: 32.0),            ),            const Expanded(              child: Padding(                padding: EdgeInsets.fromLTRB(0.0, 0.0, 0.0, 40.0),                child: Align(                  alignment: FractionalOffset.bottomCenter,                  child: CircularProgressIndicator(                    color: Colors.white,                  ),                ),              ),            ),          ],        ),      ),    );  }}